function DecideTargetPressure( temp, bar ) {
    var output = 0;
    var abort = false;
    var direction;

    switch( true ) {
        case temp < 50:
                abort = true;
        break;
        case temp >= 50 && temp <= 55:
            output = 14;
        break;
        case temp >= 55 && temp <= 60:
            output = 16;
        break;
        case temp >= 60 && temp <= 65:
            output = 18;
        break;
        case temp >= 65 && temp <= 70:
            output = 20;
        break;
        case temp >= 70 && temp <= 75:
            output = 22;
        break;
        case temp >= 75 && temp <= 80:
            output = 24;
        break;
        case temp >= 80 && temp <= 85:
            output = 26;
        break;
        case temp >= 85 && temp <= 90:
            output = 28;
        break;
        case temp >= 90 && temp <= 95:
            output = 30;
        break;
        case temp > 95:
            abort = true;
        break;

    }


    // Decide Bar will Up or Down

    if ( output < bar ) {
        direction = 'up'
    } else {
        direction = 'down'
    }
    return {
        output,
        abort,
        direction
    }
}

